import express from "express";
import { verifyTokenMiddleWare } from "../middleWares/verifyTokenMiddleWare";
import { verifyAllowedRoleMiddleWare } from "../middleWares/verifyAllowedRole";
import { deleteEventController, getApprovedEventController, getEventBySearchController, getEventByStatusController, getOrganizedEventcontroller, postEventController, updateEventController, } from "../controller/event.Controller";
const eventRoutes = express.Router();
eventRoutes.post("/", verifyTokenMiddleWare, verifyAllowedRoleMiddleWare("ORGANIZER", "ADMIN"), postEventController);
eventRoutes.get("/organizedevent/:userId", verifyTokenMiddleWare, verifyAllowedRoleMiddleWare("ORGANIZER", "ADMIN"), getOrganizedEventcontroller);
eventRoutes.get("/searchevent", verifyTokenMiddleWare, verifyAllowedRoleMiddleWare("ORGANIZER", "ADMIN", "USER"), getEventBySearchController);
eventRoutes.get("/approvedevent", verifyTokenMiddleWare, verifyAllowedRoleMiddleWare("ADMIN", "ORGANIZER", "USER"), getApprovedEventController);
eventRoutes.get("/:status", verifyTokenMiddleWare, verifyAllowedRoleMiddleWare("ADMIN"), getEventByStatusController);
eventRoutes.post("/update", verifyTokenMiddleWare, verifyAllowedRoleMiddleWare("ADMIN"), updateEventController);
eventRoutes.delete("/delete/:id", verifyTokenMiddleWare, verifyAllowedRoleMiddleWare("ADMIN", "ORGANIZER"), deleteEventController);
export default eventRoutes;
